INSERT INTO `news` (`id`, `newsname`, `newalt`, `newintro`, `newcontent`, `newimg`, `newkeywords`, `newtag`, `newuser`, `newnumber`, `idlistnew`, `idmodnew`, `slug`, `status`, `view_count`, `created_at`, `updated_at`) VALUES
(80, 'Minh Hằng từng muốn yêu người như Đức Trí và Trấn Thành', 'minh-hang-tung-muon-yeu-nguoi-nhu-duc-tri-va-tran-thanh', 'Nữ ca sĩ tiết lộ Trấn Thành, nhạc sĩ Đức Trí có nhiều điểm giống mẫu người đàn ông cô thích khi còn nhỏ.', '<p>Kh&aacute;ch mời của chương tr&igrave;nh Chuyện tối nay với Th&agrave;nh ph&aacute;t s&oacute;ng tối 12/10 l&agrave; ca sĩ Minh Hằng. Mở m&agrave;n chương tr&igrave;nh nữ ca sĩ thể hiện bằng ca kh&uacute;c&nbsp;<em>Anh từ đ&acirc;u</em>&nbsp;(nhạc phim&nbsp;<em>Bao giờ c&oacute; y&ecirc;u nhau</em>) dưới sự d&agrave;n dựng của ban nhạc Đức Tr&iacute;.</p>\r\n\r\n<p>N&oacute;i về Minh Hằng người ta n&oacute;i về một c&ocirc; g&aacute;i th&agrave;nh đạt, xinh đẹp, t&agrave;i giỏi. Hiện, c&ocirc; đ&atilde; c&oacute; trong tay nh&agrave; cửa, xe cộ, t&ecirc;n tuổi, mọi thứ m&agrave; m&igrave;nh muốn. Trước nghi vấn cho rằng Minh Hằng c&oacute; hậu thuẫn mạnh, c&ocirc; phản ph&aacute;o: &ldquo;Nếu như lần đầu th&agrave;nh c&ocirc;ng th&igrave; l&agrave; do may mắn. Nhưng để giữ được tất cả đang thuộc về m&igrave;nh trong một thời gian d&agrave;i th&igrave; n&oacute; cần sự nỗ lực rất nhiều. Trong mỗi c&ocirc;ng việc, đối với người th&agrave;nh c&ocirc;ng th&igrave; phải lu&ocirc;n vận động, s&aacute;ng tạo. Hơn nữa, l&agrave;m nghệ thuật vốn kh&ocirc;ng phải dễ d&agrave;ng&rdquo;.</p>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background-attachment:initial; background-clip:initial; background-image:initial; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:14px 0px; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\">\r\n <tbody>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\"><img alt=\"Minh Hang tung muon yeu nguoi nhu Duc Tri va Tran Thanh hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w660/Uploaded/ohunuai/2017_10_13/MINH_HANG_TAP_313.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:auto; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:660px\" /></td>\r\n   </tr>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\">Minh Hằng trải l&ograve;ng trong chương tr&igrave;nh. Ảnh:&nbsp;<em>BTC.</em></td>\r\n    </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p>Khi được hỏi về việc th&agrave;nh c&ocirc;ng qu&aacute; sớm dẫn đến nhiều &aacute;p lực m&agrave; c&ocirc; phải g&aacute;nh chịu, giọng ca xinh đẹp cho biết đ&ocirc;i l&uacute;c c&ocirc; bị mất phương hướng v&agrave; mất đi bản t&iacute;nh vốn c&oacute; của m&igrave;nh. Khi trải qua nhiều kh&oacute; khăn v&agrave; cố gắng n&iacute;u giữ th&agrave;nh c&ocirc;ng th&igrave; c&ocirc; c&agrave;ng trưởng th&agrave;nh hơn v&agrave; tất cả điều đ&oacute; lại trở th&agrave;nh kinh nghiệm qu&yacute; b&aacute;u cho bản th&acirc;n.</p>\r\n\r\n<p>Trước quan niệm người đ&agrave;n &ocirc;ng sẽ kh&oacute; giữ ch&acirc;n được những c&ocirc; g&aacute;i xinh đẹp, t&agrave;i giỏi, Minh Hằng chia sẻ:&nbsp;&ldquo;T&ocirc;i lại thấy như vậy c&agrave;ng hấp dẫn. V&agrave; người đ&agrave;n &ocirc;ng phải lu&ocirc;n nỗ lực v&igrave; c&ocirc; ấy, ở b&ecirc;n cạnh c&ocirc; ấy, phải chinh phục th&igrave; t&ocirc;i nghĩ t&igrave;nh y&ecirc;u đ&oacute; lại c&agrave;ng hấp dẫn v&agrave; th&uacute; vị hơn&rdquo;.</p>\r\n\r\n<p>Trong buổi tr&ograve; chuyện, Minh Hằng cũng n&oacute;i về mẫu đ&agrave;n &ocirc;ng của m&igrave;nh. C&ocirc; cho biết từ b&eacute; đ&atilde; ước mơ về người đ&agrave;n &ocirc;ng t&agrave;i hoa như Đức Tr&iacute;, l&atilde;ng mạn v&agrave; c&oacute; duy&ecirc;n như Trấn Th&agrave;nh. Nhưng m&agrave; c&agrave;ng lớn th&igrave; những ti&ecirc;u chuẩn đ&oacute; phai dần, hiện c&ocirc; cần một người đ&agrave;n &ocirc;ng điềm tĩnh, vững ch&atilde;i.</p>\r\n\r\n<p>Minh Hằng chia sẻ th&ecirc;m c&ocirc; từng bị quay lưng v&agrave; nếm những đau khổ ấy. Nhắc lại khoảng thời gian đ&oacute;, Minh Hằng cho biết c&ocirc; cũng kh&oacute;c rất nhiều, tự nhốt m&igrave;nh trong ph&ograve;ng, g&agrave;o kh&oacute;c đi&ecirc;n cuồng v&agrave; kh&ocirc;ng ăn uống.</p>', 'w6Xnr4_minh-hang-tap-316.jpg', NULL, NULL, 'Supper admin', 1, 33, 13, 'minh-hang-tung-muon-yeu-nguoi-nhu-duc-tri-va-tran-thanh', 1, 15, '2017-10-14 03:53:18', '2019-01-24 01:24:05'),
(81, 'Hải quân Mỹ dùng la bàn và hải đồ giấy sau các vụ đâm tàu', 'hai-quan-my-dung-la-ban-va-hai-do-giay-sau-cac-vu-dam-tau', 'Hải quân Mỹ đã yêu cầu các tàu chiến quay lại sử dụng la bàn và vẽ hải trình bằng tay để theo dõi các mối nguy hiểm sau những vụ tai nạn chết người trong năm 2017', '<p style=\"text-align:justify\"><em>New York Times</em>&nbsp;cho biết Hải qu&acirc;n Mỹ đ&atilde; thực hiện một loạt c&aacute;c biện ph&aacute;p khẩn cấp để tr&aacute;nh lặp lại c&aacute;c sự cố đ&aacute;ng tiếc sau 4 vụ tai nạn của c&aacute;c chiến hạm ở Th&aacute;i B&igrave;nh Dương, trong đ&oacute; c&oacute; 2 vụ va chạm g&acirc;y chết người.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c giải ph&aacute;p cấp b&aacute;ch được đưa ra gồm cho thủy thủ đo&agrave;n nghỉ ngơi nhiều hơn v&agrave; kh&ocirc;ng l&agrave;m việc qu&aacute; 100 giờ/tuần. Những thủy thủ thiếu giấy chứng nhận hoạt động tr&ecirc;n biển sẽ kh&ocirc;ng được l&ecirc;n t&agrave;u cho đến khi ho&agrave;n th&agrave;nh qu&aacute; tr&igrave;nh đ&agrave;o tạo. Những t&agrave;u chiến khi di chuyển v&agrave;o v&ugrave;ng biển nhiều t&agrave;u thuyền qua lại phải ph&aacute;t s&oacute;ng vị tr&iacute; của họ cho c&aacute;c t&agrave;u kh&aacute;c.</p>\r\n\r\n<h3 style=\"text-align:center\">Hạn chế d&ugrave;ng cảm biến hiện đại</h3>\r\n\r\n<p style=\"text-align:justify\">T&agrave;u chiến của Hải qu&acirc;n Mỹ đều được trang bị hệ thống nhận dạng tự động (ASI) để ph&aacute;t hiện vị tr&iacute; v&agrave; tốc độ c&aacute;c t&agrave;u di chuyển xung quanh. Tuy nhi&ecirc;n, c&aacute;c thuyền trưởng thường ra lệnh tắt hệ thống ASI v&igrave; kh&ocirc;ng muốn tiết lộ về vị tr&iacute; hoạt động của chiến hạm Mỹ. Điều n&agrave;y sẽ được thay đổi theo sắc lệnh mới.</p>\r\n\r\n<p style=\"text-align:justify\">Những biện ph&aacute;p khẩn cấp của Hải qu&acirc;n Mỹ kh&ocirc;ng phải l&agrave; mới v&agrave; đ&oacute; l&agrave; những quy định r&otilde; r&agrave;ng trong hoạt động tr&ecirc;n biển của hải qu&acirc;n nhưng đ&atilde; bị phớt lờ trong nhiều năm. Hải qu&acirc;n Mỹ chỉ &aacute;p dụng c&aacute;c biện ph&aacute;p n&agrave;y sau khi xảy ra 2 vụ tai nạn li&ecirc;n tiếp chỉ trong 2 th&aacute;ng khiến 17 thủy thủ thiệt mạng.</p>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background-attachment:initial; background-clip:initial; background-image:initial; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:40px 0px 40px -110px; outline:0px; padding:0px; text-align:justify; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:820px\">\r\n  <tbody>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\"><img alt=\"Hai quan My dung la ban va hai do giay sau cac vu dam tau hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/bpmoqwq1/2017_09_29/USS_Vincennes_CG49_Aegis_large_screen_displays.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:auto; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:860px\" title=\"Hải quân Mỹ dùng la bàn và hải đồ giấy sau các vụ đâm tàu hình ảnh 1\" /></td>\r\n   </tr>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\">Chiến hạm Mỹ được trang bị h&agrave;ng loạt cảm biến hiện đại nhưng &iacute;t khi sử dụng để tr&aacute;nh lộ vị tr&iacute; của t&agrave;u. Ảnh:&nbsp;<em>Hải qu&acirc;n Mỹ.</em></td>\r\n   </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c vấn đề đang xảy ra với Hải qu&acirc;n Mỹ đ&atilde; được c&aacute;c cơ quan gi&aacute;m s&aacute;t của Quốc hội v&agrave; chuy&ecirc;n gia hải qu&acirc;n cảnh b&aacute;o từ năm 2010.</p>\r\n\r\n<p style=\"text-align:justify\">&ldquo;Nhiều vấn đề ch&uacute;ng ta đang thảo luận h&ocirc;m nay đ&atilde; được c&aacute;c nh&agrave; l&atilde;nh đạo hải qu&acirc;n biết đến trong nhiều năm. L&agrave;m thế n&agrave;o để giải th&iacute;ch với ch&uacute;ng t&ocirc;i điều đ&oacute;, Đ&ocirc; đốc&rdquo;, Thượng nghị sĩ John McCain, Chủ tịch Ủy ban Qu&acirc;n vụ Thượng viện chất vấn Đ&ocirc; đốc John M.Richardson, Tư lệnh Chiến dịch Hải qu&acirc;n Mỹ trong phi&ecirc;n điều trần tại Quốc hội.</p>\r\n\r\n<p style=\"text-align:justify\">Đ&ocirc; đốc Richardson đ&atilde; kh&ocirc;ng thể đưa được lời giải th&iacute;ch n&agrave;o. Chỉ thị mới được ban h&agrave;nh bởi Ph&oacute; đ&ocirc; đốc Thomas Rowden, Tư lệnh Hải qu&acirc;n Mỹ ở Th&aacute;i B&igrave;nh Dương. Mệnh lệnh được đưa ra dựa tr&ecirc;n c&aacute;c dữ liệu m&agrave; c&aacute;c chỉ huy thu thập trong qu&aacute; tr&igrave;nh đ&igrave;nh chỉ hoạt động tr&ecirc;n to&agrave;n thế giới v&agrave;o th&aacute;ng trước.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c quan chức v&agrave; cựu quan chức hải qu&acirc;n cho biết quy định mới đ&aacute;nh dấu một số thay đổi quan trọng về văn h&oacute;a hoạt động của Hải qu&acirc;n Mỹ.&nbsp; Sự an to&agrave;n trong hoạt động của c&aacute;c t&agrave;u chiến phải được đặt l&ecirc;n h&agrave;ng đầu.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c chỉ huy y&ecirc;u cầu thủy thủ phải sử dụng la b&agrave;n, b&uacute;t ch&igrave; để vẽ hải tr&igrave;nh v&agrave; đ&aacute;nh dấu c&aacute;c mối nguy hiểm tiềm ẩn. C&aacute;c quy tắc hoạt động d&agrave;nh cho thủy thủ đo&agrave;n l&agrave;m nhiệm vụ điều hướng khi thuyền trưởng kh&ocirc;ng c&oacute; mặt tr&ecirc;n th&aacute;p chỉ huy đều được xem x&eacute;t lại.</p>\r\n\r\n<h3 style=\"text-align:center\">Tai nạn v&igrave; qu&aacute; tải</h3>\r\n\r\n<p style=\"text-align:justify\">Trong 9 th&aacute;ng đầu năm 2017, c&aacute;c t&agrave;u chiến của Hải qu&acirc;n Mỹ hoạt động ở Th&aacute;i B&igrave;nh Dương đ&atilde; xảy ra 4 vụ tai nạn, trong đ&oacute; c&oacute; 2 vụ tai nạn thảm khốc. Ng&agrave;y 17/6, t&agrave;u khu trục USS Fitzgerald va chạm với t&agrave;u container ACX Crystal ngo&agrave;i khơi biển Nhật Bản khiến 7 thủy thủ thiệt mạng.</p>\r\n\r\n<table align=\"center\" class=\"picture\" style=\"-webkit-font-smoothing:antialiased; background-attachment:initial; background-clip:initial; background-image:initial; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px; box-sizing:border-box; color:rgb(85, 85, 85); font-family:sans-serif; font-size:14px; line-height:20px; margin:40px 0px 40px -110px; outline:0px; padding:0px; text-align:justify; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:820px\">\r\n <tbody>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\"><img alt=\"Hai quan My dung la ban va hai do giay sau cac vu dam tau hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/bpmoqwq1/2017_09_29/GettyImages_8360780540.jpg\" style=\"background:transparent; border:0px; box-sizing:border-box; display:block; height:auto; margin:0px; max-width:100%; outline:0px; padding:0px; text-rendering:geometricPrecision; text-size-adjust:100%; vertical-align:baseline; width:860px\" title=\"Hải quân Mỹ dùng la bàn và hải đồ giấy sau các vụ đâm tàu hình ảnh 2\" /></td>\r\n   </tr>\r\n   <tr>\r\n      <td style=\"vertical-align:baseline\">T&agrave;u khu trục USS John S. McCain bị m&oacute;p một lỗ lớn ở mạn tr&aacute;i sau vụ va chạm với t&agrave;u chở dầu trong th&aacute;ng 8. Ảnh:&nbsp;<em>Getty.</em></td>\r\n    </tr>\r\n </tbody>\r\n</table>\r\n\r\n<p style=\"text-align:justify\">Ng&agrave;y 21/8, t&agrave;u khu trục USS John S. McCain va chạm với t&agrave;u chở dầu ngo&agrave;i khơi bờ biển Singapore khiến 10 thủy thủ thiệt mạng. Cả hai t&agrave;u đều bị thiệt hại nặng về th&acirc;n t&agrave;u v&agrave; phải đưa về Mỹ để sửa chữa.</p>\r\n\r\n<p style=\"text-align:justify\">Một số chỉ huy Hải qu&acirc;n Mỹ cho rằng quy m&ocirc; hiện tại của hải qu&acirc;n nhỏ hơn nhiều, trong khi vẫn phải thực hiện những nhiệm vụ tương tự hạm đội lớn đ&atilde; l&agrave;m c&aacute;ch đ&acirc;y một thập kỷ. C&aacute;c t&agrave;u chiến phải triển khai li&ecirc;n tục khiến thủy thủ đo&agrave;n c&oacute; &iacute;t thời gian để nghỉ ngơi, đ&agrave;o tạo v&agrave; bảo tr&igrave;.</p>\r\n\r\n<p style=\"text-align:justify\">Tướng Joseph F. Dunford Jr, Chủ tịch Hội đồng Tham mưu trưởng li&ecirc;n qu&acirc;n&nbsp; đ&atilde; n&oacute;i với Quốc hội trong phi&ecirc;n điều trần v&agrave;o ng&agrave;y 25/9 về chuyến thăm của &ocirc;ng l&ecirc;n t&agrave;u khu trục USS Barry. Theo tướng Dunford, con t&agrave;u đ&atilde; ở tr&ecirc;n biển tới 70% thời gian trong 12 th&aacute;ng.</p>\r\n\r\n<p style=\"text-align:justify\">Một b&aacute;o c&aacute;o của Văn ph&ograve;ng Kiểm to&aacute;n Ch&iacute;nh phủ c&ocirc;ng bố trong th&aacute;ng 5 cho biết c&aacute;c thủy thủ phải l&agrave;m việc đến 108 giờ/tuần. Trường Cao học Hải qu&acirc;n Mỹ sẽ tiến h&agrave;nh nghi&ecirc;n cứu ngắn về t&aacute;c động ti&ecirc;u cực về mặt sinh học đối với thủy thủ đo&agrave;n l&agrave;m việc qu&aacute; sức.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhi&ecirc;n, Thượng nghị sĩ McCain cho rằng nếu c&aacute;c thủy thủ thực sự phải l&agrave;m việc 100 giờ/tuần th&igrave; kh&ocirc;ng cần phải tiến h&agrave;nh nghi&ecirc;n cứu nữa. Theo &ocirc;ng, hải qu&acirc;n đang cố t&igrave;m một c&aacute;i cớ để biện minh cho những yếu k&eacute;m trong hoạt động thời gian gần đ&acirc;y.</p>\r\n\r\n<div class=\"player\" style=\"box-sizing: border-box; text-rendering: geometricPrecision; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 17.6px; vertical-align: baseline; background: transparent; text-size-adjust: 100%; width: 860px; position: relative; height: 484px;\">\r\n<div class=\"video autoplay muted playing\" id=\"video81694\" style=\"box-sizing: border-box; text-rendering: geo